book_14 = Book.create!(google_book_id: "SdFYPgAACAAJ", book_title: "Moving Mars", author: "Greg Bear", isbn_13: "9781417670154", pg_count: 500, description: "For use in schools and libraries only. In a scenario based on the colonization of our neighboring planet by corporate interests, discontent on the part of the second and third generations of the original colonists lays the groundwork for a revolution destined to last for decades.", condition: "Excellent", category: "Fiction", book_cover: "http://books.google.com/books/content?id=SdFYPgAACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api", available: true)
